import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Snackbar,
  Alert,
  Badge,
  Pagination,
  Box
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Cart from './Cart';
import OrderConfirmation from './OrderConfirmation';
import ProductImageModal from './ProductImageModal';

const Marketplace = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [cartOpen, setCartOpen] = useState(false);
  const [confirmationOpen, setConfirmationOpen] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [imageModalOpen, setImageModalOpen] = useState(false);
  const [page, setPage] = useState(1);
  const productsPerPage = 36; // Aumentamos a 36 productos por página para que coincida con la paginación original

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8080/api/products');
      setProducts(response.data);
    } catch (error) {
      setError('Error al cargar los productos');
    } finally {
      setLoading(false);
    }
  };

  // Calcular productos para la página actual
  const getCurrentPageProducts = () => {
    const startIndex = (page - 1) * productsPerPage;
    const endIndex = startIndex + productsPerPage;
    return products.slice(startIndex, endIndex);
  };

  const handlePageChange = (event, value) => {
    setPage(value);
    window.scrollTo(0, 0); // Scroll al inicio cuando cambia la página
  };

  const addToCart = (product) => {
    setCart(currentCart => {
      const existingItem = currentCart.find(item => item.code === product.code);
      if (existingItem) {
        return currentCart.map(item =>
          item.code === product.code
            ? { ...item, quantity: item.quantity + product.bulkQuantity }
            : item
        );
      }
      return [...currentCart, { ...product, quantity: product.bulkQuantity }];
    });
    setSnackbar({
      open: true,
      message: `${product.name} agregado al carrito`,
      severity: 'success'
    });
  };

  const updateCartItemQuantity = (item, newQuantity) => {
    if (newQuantity < 1) return;
    setCart(currentCart =>
      currentCart.map(cartItem =>
        cartItem.code === item.code
          ? { ...cartItem, quantity: newQuantity }
          : cartItem
      )
    );
  };

  const removeFromCart = (item) => {
    setCart(currentCart => currentCart.filter(cartItem => cartItem.code !== item.code));
  };

  const handleCheckout = () => {
    const total = cart.reduce((sum, item) => {
      const pricePerUnit = (item.price * 0.44) / item.bulkQuantity;
      return sum + (pricePerUnit * item.quantity);
    }, 0);

    if (total < 300000) {
      setSnackbar({
        open: true,
        message: 'El monto mínimo de compra es de $300.000',
        severity: 'error'
      });
      return;
    }

    setCartOpen(false);
    setConfirmationOpen(true);
  };

  const handleConfirmOrder = async () => {
    const total = cart.reduce((sum, item) => {
      const pricePerUnit = item.price * 0.44;
      return sum + (pricePerUnit * item.quantity);
    }, 0);

    if (total < 300000) {
      setSnackbar({
        open: true,
        message: 'El monto mínimo de compra es de $300.000',
        severity: 'error'
      });
      return;
    }

    try {
      await axios.post('http://localhost:8080/api/order', {
        items: cart,
        email: 'matiasgastron@gmail.com'
      });

      setCart([]);
      setConfirmationOpen(false);
      setSnackbar({
        open: true,
        message: '¡Tu pedido ha sido realizado con éxito! Pronto nos contactaremos contigo.',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Error al procesar el pedido',
        severity: 'error'
      });
    }
  };

  if (loading) return <div>Cargando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <Container>
      <Button
        color="primary"
        onClick={() => setCartOpen(true)}
        style={{ 
          position: 'fixed', 
          bottom: 20,
          right: 20,
          zIndex: 1000,
          backgroundColor: '#1976d2',
          color: 'white',
          padding: '12px',
          minWidth: 'unset',
          borderRadius: '50%',
          boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.3)'
        }}
      >
        <Badge badgeContent={cart.length} color="error">
          <ShoppingCartIcon />
        </Badge>
      </Button>

      <Grid container spacing={3} sx={{ mt: 3 }}>
        {getCurrentPageProducts().map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product.code}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={product.imageUrl}
                alt={product.name}
                sx={{ 
                  objectFit: 'contain',
                  backgroundColor: '#f5f5f5',
                  cursor: 'pointer'
                }}
                onClick={() => {
                  setSelectedProduct(product);
                  setImageModalOpen(true);
                }}
              />
              <CardContent>
                <Typography gutterBottom variant="h6">
                  {product.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Código: {product.code}
                </Typography>
                <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <span style={{ color: '#1976d2' }}>
                    ${(product.price * 0.44 / product.bulkQuantity).toLocaleString('es-AR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                  </span>
                  <span style={{ 
                    color: 'rgba(0, 0, 0, 0.7)', 
                    fontSize: '0.8em',
                    fontWeight: 'normal' 
                  }}>
                    por unidad
                  </span>
                </Typography>
                <Typography variant="body2">
                  Bulto: {product.bulkQuantity} unidades
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Precio por bulto: ${(product.price * 0.44).toLocaleString('es-AR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                </Typography>
                {product.dimensions && (
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                    Dimensiones: {product.dimensions}
                  </Typography>
                )}
                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => addToCart(product)}
                  sx={{ mt: 2 }}
                >
                  Agregar al Carrito
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Paginación */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'center', 
        mt: 4, 
        mb: 4,
        '& .MuiPagination-ul': { // Estilo para los números de página
          gap: '8px'
        }
      }}>
        <Pagination 
          count={Math.ceil(products.length / productsPerPage)}
          page={page}
          onChange={handlePageChange}
          color="primary"
          size="large"
          siblingCount={1} // Reduce el número de páginas mostradas a los lados
          boundaryCount={1} // Reduce el número de páginas mostradas en los extremos
        />
      </Box>

      <ProductImageModal
        open={imageModalOpen}
        onClose={() => setImageModalOpen(false)}
        product={selectedProduct}
      />

      <Cart
        open={cartOpen}
        onClose={() => setCartOpen(false)}
        cart={cart}
        onUpdateQuantity={updateCartItemQuantity}
        onRemoveItem={removeFromCart}
        onCheckout={handleCheckout}
      />

      <OrderConfirmation
        open={confirmationOpen}
        onClose={() => setConfirmationOpen(false)}
        cart={cart}
        onConfirm={handleConfirmOrder}
      />

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default Marketplace;
