import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Typography,
  Box,
  Alert,
  TextField
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

const MINIMUM_PURCHASE = 300000;

const Cart = ({ open, onClose, cart, onUpdateQuantity, onRemoveItem, onCheckout }) => {
  const handleQuantityChange = (item, event) => {
    const newValue = event.target.value;

    // Permitir campo vacío para que el usuario pueda borrar
    if (newValue === '') {
      onUpdateQuantity(item, 0);
      return;
    }

    // Convertir a número
    const value = parseInt(newValue, 10);

    // Si no es un número válido, retornar
    if (isNaN(value)) return;

    // Actualizar aunque no sea múltiplo para permitir escritura
    onUpdateQuantity(item, value);
  };

  const handleIncrement = (item) => {
    onUpdateQuantity(item, item.quantity + item.bulkQuantity);
  };

  const handleDecrement = (item) => {
    if (item.quantity - item.bulkQuantity >= 0) {
      onUpdateQuantity(item, item.quantity - item.bulkQuantity);
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const pricePerUnit = item.price * 0.44;
      return total + (pricePerUnit * item.quantity);
    }, 0);
  };

  const total = calculateTotal();
  const isbelowMinimum = total < MINIMUM_PURCHASE;

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>Carrito de Compras</DialogTitle>
      <DialogContent>
        {cart.length === 0 ? (
          <Typography>El carrito está vacío</Typography>
        ) : (
          <>
            <List>
              {cart.map((item) => (
                <ListItem key={item.code}>
                  <ListItemText
                    primary={item.name}
                    secondary={`Código: ${item.code} - Precio por unidad: $${(item.price * 0.44).toLocaleString('es-AR', { minimumFractionDigits: 2 })}`}
                  />
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <IconButton onClick={() => handleDecrement(item)}>
                      <RemoveIcon />
                    </IconButton>
                    <TextField
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item, e)}
                      onBlur={(e) => {
                        // Al perder el foco, asegurarse que sea múltiplo
                        const value = parseInt(e.target.value, 10);
                        if (value % item.bulkQuantity !== 0) {
                          // Redondear al múltiplo más cercano
                          const roundedValue = Math.ceil(value / item.bulkQuantity) * item.bulkQuantity;
                          onUpdateQuantity(item, roundedValue);
                        }
                      }}
                      type="number"
                      size="small"
                      sx={{ width: '80px' }}
                      inputProps={{
                        min: 0,
                        step: item.bulkQuantity
                      }}
                      helperText={`Múltiplos de ${item.bulkQuantity}`}
                    />
                    <IconButton onClick={() => handleIncrement(item)}>
                      <AddIcon />
                    </IconButton>
                    <IconButton onClick={() => onRemoveItem(item)}>
                      <DeleteIcon />
                    </IconButton>
                  </Box>
                </ListItem>
              ))}
            </List>
            <Box sx={{ mt: 2, mb: 2 }}>
              <Typography variant="h6">
                Total: ${total.toLocaleString('es-AR', { minimumFractionDigits: 2 })}
              </Typography>
              {isbelowMinimum && (
                <Alert severity="warning" sx={{ mt: 2 }}>
                  El monto mínimo de compra es de ${MINIMUM_PURCHASE.toLocaleString('es-AR')}
                </Alert>
              )}
            </Box>
          </>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cerrar</Button>
        <Button 
          onClick={onCheckout}
          variant="contained" 
          color="primary"
          disabled={cart.length === 0 || isbelowMinimum}
        >
          Finalizar Compra
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default Cart;
