import React from 'react';
import {
  Dialog,
  DialogContent,
  DialogTitle,
  IconButton,
  Box,
  Typography
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const ProductImageModal = ({ open, onClose, product }) => {
  console.log('Product in modal:', product); // Para debug

  return (
    <Dialog 
      open={open} 
      onClose={onClose}
      maxWidth="md"
      fullWidth
    >
      <DialogTitle>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          {product?.name}
          <IconButton onClick={onClose}>
            <CloseIcon />
          </IconButton>
        </Box>
      </DialogTitle>
      <DialogContent>
        <Box 
          sx={{ 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center',
            gap: 2
          }}
        >
          <img
            src={product?.imageUrl}
            alt={product?.name}
            style={{
              maxWidth: '100%',
              maxHeight: '70vh',
              objectFit: 'contain'
            }}
          />
          {product?.dimensions && (
            <Typography 
              variant="body1" 
              color="text.secondary"
              sx={{ 
                mt: 2,
                textAlign: 'center',
                width: '100%'
              }}
            >
              Dimensiones: {product.dimensions}
            </Typography>
          )}
        </Box>
      </DialogContent>
    </Dialog>
  );
};

export default ProductImageModal;
