import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Paper, Typography, Box } from '@mui/material';
import { API_BASE_URL } from '../config';

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchMyOrders = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/orders/user/${userId}`);
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchMyOrders();
  }, [userId]);

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 4 }}>Mis Pedidos</Typography>
      {orders.length === 0 ? (
        <Typography>No tienes pedidos realizados</Typography>
      ) : (
        orders.map(order => (
          <Paper 
            key={order.orderNumber}
            sx={{ 
              p: 3, 
              mb: 3,
              backgroundColor: 'white',
              border: '1px solid #ddd'
            }}
          >
            <Typography variant="h6" color="primary" gutterBottom>
              Orden #{order.orderNumber}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Fecha: {new Date(order.orderDate).toLocaleString()}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Estado: {order.status}
            </Typography>

            <Box sx={{ mt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Productos:
              </Typography>
              {order.items.map((item, index) => (
                <Box key={index} sx={{ ml: 2, mb: 2 }}>
                  <Typography variant="body1">
                    {item.productName} - CÃ³digo: {item.productCode}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Cantidad: {item.quantity} unidades
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Precio por unidad: ${item.pricePerUnit.toLocaleString('es-AR', { minimumFractionDigits: 2 })}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Subtotal: ${item.total.toLocaleString('es-AR', { minimumFractionDigits: 2 })}
                  </Typography>
                </Box>
              ))}
            </Box>

            <Typography variant="h6" sx={{ mt: 2, color: 'primary.main' }}>
              Total: ${order.total.toLocaleString('es-AR', { minimumFractionDigits: 2 })}
            </Typography>
          </Paper>
        ))
      )}
    </Container>
  );
};

export default MyOrders;
