import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  List,
  ListItem,
  ListItemText,
  Typography,
  Box
} from '@mui/material';

const OrderConfirmation = ({ open, onClose, cart, onConfirm }) => {
  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const pricePerUnit = item.price * 0.44; // Aplicamos la misma fórmula
      return total + (pricePerUnit * item.quantity);
    }, 0);
  };

  const total = calculateTotal();

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>Confirmar Pedido</DialogTitle>
      <DialogContent>
        <List>
          {cart.map((item) => (
            <ListItem key={item.code}>
              <ListItemText
                primary={item.name}
                secondary={
                  <>
                    Código: {item.code}
                    <br />
                    Precio por unidad: ${(item.price * 0.44).toLocaleString('es-AR', { minimumFractionDigits: 2 })}
                    <br />
                    Cantidad: {item.quantity}
                    <br />
                    Subtotal: ${(item.price * 0.44 * item.quantity).toLocaleString('es-AR', { minimumFractionDigits: 2 })}
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
        <Box sx={{ mt: 2 }}>
          <Typography variant="h6">
            Total del Pedido: ${total.toLocaleString('es-AR', { minimumFractionDigits: 2 })}
          </Typography>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancelar</Button>
        <Button onClick={onConfirm} variant="contained" color="primary">
          Confirmar Pedido
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default OrderConfirmation;
