import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  Paper,
  CircularProgress,
  Chip
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

const Marketplace = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      console.log('Solicitando productos al backend...');
      const baseUrl = process.env.NODE_ENV === 'development' 
        ? 'http://localhost:8080' 
        : '';
      console.log('URL de la API:', `${baseUrl}/api/products`);
      const response = await axios.get(`${baseUrl}/api/products`);
      console.log('Productos recibidos:', response.data);
      setProducts(response.data);
      setError(null);
    } catch (error) {
      console.error('Error completo:', error);
      if (error.response) {
        console.error('Respuesta del servidor:', error.response.data);
        console.error('Estado HTTP:', error.response.status);
      } else if (error.request) {
        console.error('No se recibió respuesta del servidor');
      }
      setError('Error al cargar los productos. Por favor, intente nuevamente.');
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleOrder = async () => {
    try {
      await axios.post('/api/order', {
        products: cart,
        userEmail: 'valentingastron@hotmail.com'
      });
      alert('¡Orden realizada con éxito!');
      setCart([]);
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Error al realizar la orden. Por favor, intente nuevamente.');
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  return (
    <Container>
      <Grid container spacing={3} sx={{ mt: 3 }}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product.code}>
            <Card>
              {product.imageUrl && (
                <CardMedia
                  component="img"
                  height="200"
                  image={product.imageUrl}
                  alt={product.name}
                />
              )}
              <CardContent>
                <Typography gutterBottom variant="h6">
                  {product.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Código: {product.code}
                </Typography>
                <Typography variant="h6" color="primary">
                  ${product.price?.toLocaleString()}
                </Typography>
                {product.category && (
                  <Chip 
                    label={product.category} 
                    size="small" 
                    sx={{ mt: 1 }} 
                  />
                )}
                {product.bulkQuantity && (
                  <Typography variant="body2">
                    Bulto: {product.bulkQuantity} unidades
                  </Typography>
                )}
                <Button
                  variant="contained"
                  size="small"
                  onClick={() => addToCart(product)}
                  sx={{ mt: 2 }}
                  fullWidth
                >
                  Agregar al Carrito
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {cart.length > 0 && (
        <Paper
          elevation={3}
          sx={{
            position: 'fixed',
            bottom: 20,
            right: 20,
            p: 2,
            zIndex: 1000,
            width: 300
          }}
        >
          <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
            <ShoppingCartIcon sx={{ mr: 1 }} />
            Carrito ({cart.length} items)
          </Typography>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleOrder}
            sx={{ mt: 2 }}
          >
            Realizar Pedido
          </Button>
        </Paper>
      )}
    </Container>
  );
};

export default Marketplace;
