import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Snackbar,
  Alert,
  Badge
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Cart from './Cart';
import OrderConfirmation from './OrderConfirmation';

const Marketplace = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [cartOpen, setCartOpen] = useState(false);
  const [confirmationOpen, setConfirmationOpen] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8080/api/products');
      setProducts(response.data);
    } catch (error) {
      setError('Error al cargar los productos');
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (product) => {
    setCart(currentCart => {
      const existingItem = currentCart.find(item => item.code === product.code);
      if (existingItem) {
        return currentCart.map(item =>
          item.code === product.code
            ? { ...item, quantity: item.quantity + product.bulkQuantity }
            : item
        );
      }
      return [...currentCart, { ...product, quantity: product.bulkQuantity }];
    });
    setSnackbar({
      open: true,
      message: `${product.name} agregado al carrito`,
      severity: 'success'
    });
  };

  const updateCartItemQuantity = (item, newQuantity) => {
    if (newQuantity < 1) return;
    setCart(currentCart =>
      currentCart.map(cartItem =>
        cartItem.code === item.code
          ? { ...cartItem, quantity: newQuantity }
          : cartItem
      )
    );
  };

  const removeFromCart = (item) => {
    setCart(currentCart => currentCart.filter(cartItem => cartItem.code !== item.code));
  };

  const handleCheckout = () => {
    setCartOpen(false);
    setConfirmationOpen(true);
  };

  const handleConfirmOrder = async () => {
    try {
      await axios.post('http://localhost:8080/api/order', {
        items: cart,
        email: 'matiasgastron@gmail.com'
      });

      setCart([]);
      setConfirmationOpen(false);
      setSnackbar({
        open: true,
        message: '¡Tu pedido ha sido realizado con éxito! Pronto nos contactaremos contigo.',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Error al procesar el pedido',
        severity: 'error'
      });
    }
  };

  if (loading) return <div>Cargando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <Container>
      <Button
        color="primary"
        onClick={() => setCartOpen(true)}
        style={{ position: 'fixed', top: 20, right: 20, zIndex: 1000 }}
      >
        <Badge badgeContent={cart.length} color="secondary">
          <ShoppingCartIcon />
        </Badge>
      </Button>

      <Grid container spacing={3} sx={{ mt: 3 }}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product.code}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={product.imageUrl}
                alt={product.name}
              />
              <CardContent>
                <Typography gutterBottom variant="h6">
                  {product.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Código: {product.code}
                </Typography>
                <Typography variant="h6" color="primary">
                  ${product.price?.toLocaleString()}
                </Typography>
                <Typography variant="body2">
                  Bulto: {product.bulkQuantity} unidades
                </Typography>
                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => addToCart(product)}
                  sx={{ mt: 2 }}
                >
                  Agregar al Carrito
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Cart
        open={cartOpen}
        onClose={() => setCartOpen(false)}
        cart={cart}
        onUpdateQuantity={updateCartItemQuantity}
        onRemoveItem={removeFromCart}
        onCheckout={handleCheckout}
      />

      <OrderConfirmation
        open={confirmationOpen}
        onClose={() => setConfirmationOpen(false)}
        cart={cart}
        onConfirm={handleConfirmOrder}
      />

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default Marketplace;
